mapper.update:
update clubs
set member_num = member_num+1
where id = #{clubid}

update clubs
set member_num = member_num-1
where id = #{clubid}

update user_club
set deleted = false
where user_id = #{user_id} and club_id = #{club_id}

update user_club
set deleted = true
where user_id = #{user_id} and club_id = #{club_id}
